// ========================================
// PARSE RPC RESPONSE v2.0
// ========================================
// Compatible con formato Urobot

const input = $input.first()?.json;

// El resultado viene como array de PostgreSQL
let result;

if (Array.isArray(input) && input.length > 0) {
  // Formato: [{ upsert_lead_interaction: {...} }]
  result = input[0]?.upsert_lead_interaction || input[0];
} else if (input?.upsert_lead_interaction) {
  // Formato directo con función
  result = input.upsert_lead_interaction;
} else {
  // Formato directo
  result = input;
}

// Si viene como string JSON, parsearlo
if (typeof result === 'string') {
  try {
    result = JSON.parse(result);
  } catch (e) {
    console.error('❌ Error parseando respuesta:', e);
    result = { success: false, error: 'PARSE_ERROR', message: e.message };
  }
}

// Extraer campos del JSONB
const success = result?.success || false;
const action = result?.action || 'UNKNOWN';
const isDuplicate = result?.is_duplicate || false;
const leadId = result?.lead_id || null;
const leadUuid = result?.lead_uuid || null;
const telefono = result?.telefono || null;
const telefonoOriginal = result?.telefono_original || null;
const totalInteracciones = result?.total_interacciones || 0;
const estado = result?.estado || null;
const message = result?.message || 'Procesado';
const error = result?.error || null;
const errorDetail = result?.error_detail || null;

// Logging para debugging
if (success) {
  console.log('✅ LEAD_TRACKER RPC Result:', {
    action,
    lead_id: leadId,
    total_interacciones: totalInteracciones,
    is_duplicate: isDuplicate
  });
} else {
  console.error('❌ LEAD_TRACKER Error:', {
    error,
    error_detail: errorDetail,
    message
  });
}

// Retornar formato consistente para n8n
return [{
  json: {
    success,
    action,
    is_duplicate: isDuplicate,
    lead_id: leadId,
    lead_uuid: leadUuid,
    telefono,
    telefono_original: telefonoOriginal,
    total_interacciones: totalInteracciones,
    estado,
    message,
    error,
    error_detail: errorDetail,

    // Metadata
    _timestamp: new Date().toISOString(),
    _version: '2.0',
    _source: 'LEAD_TRACKER_V2_RPC'
  }
}];
