'use strict';

/**
 * â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
 * COMBINE_BUSY v6.0.0 â€” ROBUST ERROR HANDLING
 * â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
 * 
 * CAMBIOS v6.0:
 * âœ… FIX #1: Detecta errores de API en inputs y propaga el estado
 * âœ… FIX #2: No asume "calendario libre" si hubo error de API
 * âœ… FIX #3: Mejor logging para debugging
 * âœ… FIX #4: ValidaciÃ³n estricta de slots antes de filtrar
 * 
 * â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
 */

const VERSION = '6.0.0-ROBUST';
const DEBUG = true;

const LIMITS = Object.freeze({
  MAX_SLOTS_PER_DAY: 25,
  MAX_TOTAL_SLOTS: 150,
});

const LOGS = [];
function log(level, message, data = {}) {
  const entry = { 
    timestamp: new Date().toISOString(), 
    level: level.toUpperCase(), 
    component: 'COMBINE_BUSY', 
    version: VERSION, 
    message, 
    ...data 
  };
  LOGS.push(entry);
  if (DEBUG) console.log(`[${level.toUpperCase()}] ${message}`, JSON.stringify(data));
}

const _formattersCache = new Map();
function getCachedFormatter(options, tz) {
  const key = JSON.stringify({ ...options, tz });
  let formatter = _formattersCache.get(key);
  if (!formatter) {
    formatter = new Intl.DateTimeFormat('es-MX', { timeZone: tz, ...options });
    _formattersCache.set(key, formatter);
  }
  return formatter;
}

function formatLocal(iso, tz) {
  try {
    const formatter = getCachedFormatter({ 
      hour12: false, 
      year: 'numeric', 
      month: '2-digit', 
      day: '2-digit', 
      hour: '2-digit', 
      minute: '2-digit' 
    }, tz);
    return formatter.format(new Date(iso));
  } catch {
    return iso;
  }
}

function cropToWindow(interval, startISO, endISO) {
  try {
    const s = (startISO && interval.start < startISO) ? startISO : interval.start;
    const e = (endISO && interval.end > endISO) ? endISO : interval.end;
    return (s < e) ? { start: s, end: e } : null;
  } catch {
    return null;
  }
}

function mergeExclusive(list) {
  if (!list || !list.length) return [];
  try {
    const sorted = [...list].sort((a, b) => a.start.localeCompare(b.start));
    const merged = [{ start: sorted[0].start, end: sorted[0].end }];

    for (let i = 1; i < sorted.length; i++) {
      const last = merged[merged.length - 1];
      const current = sorted[i];
      if (current.start < last.end) {
        if (current.end > last.end) last.end = current.end;
      } else {
        merged.push({ start: current.start, end: current.end });
      }
    }
    return merged;
  } catch {
    return [];
  }
}

function filterBusySlots(slots, busyBySede) {
  if (!slots || !Array.isArray(slots)) return [];

  return slots.filter(slot => {
    try {
      if (!slot || !slot.sede || !slot.start || !slot.end) return false;

      const busyList = busyBySede[slot.sede];
      if (!busyList || !busyList.length) return true;

      for (const busy of busyList) {
        // Overlap check: slot.start < busy.end AND slot.end > busy.start
        if (slot.start < busy.end && slot.end > busy.start) {
          return false;
        }
      }
      return true;
    } catch {
      return false; // En caso de error, NO permitir el slot (mÃ¡s seguro)
    }
  });
}

function loadContext() {
  let ctx = {}, requestId = `combine-${Date.now()}`;

  try {
    const setDefaultsItems = $('SET_DEFAULTS1').all();
    if (setDefaultsItems.length > 0) {
      ctx = setDefaultsItems[0].json || {};
      if (ctx.requestId) requestId = ctx.requestId;
      return { ctx, requestId, source: 'SET_DEFAULTS' };
    }
  } catch (err) {
    log('WARN', 'No se pudo cargar SET_DEFAULTS', { error: err.message });
  }

  try {
    const items = $input.all();
    for (const item of items) {
      if (item.json?.kind === 'context') {
        ctx = item.json;
        if (ctx.requestId) requestId = ctx.requestId;
        return { ctx, requestId, source: 'input_context' };
      }
    }
  } catch {}

  return { 
    ctx: { 
      tz: 'America/Mexico_City',
      windowStart: new Date().toISOString(),
      windowEnd: new Date(Date.now() + 14 * 24 * 60 * 60 * 1000).toISOString()
    }, 
    requestId, 
    source: 'fallback' 
  };
}

function loadSedes() {
  try {
    const store = $getWorkflowStaticData('global');
    if (store?.SCHEDULE?.sedes) return Object.keys(store.SCHEDULE.sedes);
  } catch {}
  return ['POLANCO', 'SATELITE'];
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// MAIN
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

try {
  const startTime = Date.now();

  const rawItems = $input.all().map(x => x.json);
  const { ctx, requestId, source: ctxSource } = loadContext();
  const SEDES = loadSedes();
  const TZ = ctx.tz || 'America/Mexico_City';
  const windowStartISO = ctx.windowStart || null;
  const windowEndISO = ctx.windowEnd || null;

  // â•â•â• DETECCIÃ“N DE ERRORES DE API â•â•â•
  const apiErrors = [];
  const sedesConError = new Set();

  for (const item of rawItems) {
    if (!item) continue;

    // Detectar errores de API propagados desde Normalize
    if (item.sourceStatus === 'API_ERROR' || item.error === true) {
      const sede = item.sede || 'UNKNOWN';
      sedesConError.add(sede);
      apiErrors.push({
        sede,
        error: item.errorMessage || item.error || 'API_ERROR',
        timestamp: new Date().toISOString()
      });
      log('ERROR', `API error detectado para sede ${sede}`, { error: item.errorMessage });
    }
  }

  // Si TODAS las sedes tienen error, NO podemos dar horarios confiables
  if (sedesConError.size >= SEDES.length) {
    log('CRITICAL', 'TODAS las sedes tienen error de API - no se pueden mostrar horarios');
    return [{
      json: {
        kind: 'busy_combined',
        build: { version: VERSION, generatedAt: new Date().toISOString() },
        requestId,
        ctx,
        scheduleGate: { allowedSlots: [] },
        busyBySede: {},
        metrics: {
          apiErrors: apiErrors.length,
          sedesConError: [...sedesConError],
          allSedesFailed: true
        },
        error: 'ALL_CALENDARS_FAILED',
        errorMessage: 'No se pudo conectar con los calendarios. Por favor intenta de nuevo.',
        _logs: LOGS
      }
    }];
  }

  // Extraer Busy Intervals (solo de sedes SIN error)
  const groups = Object.fromEntries(SEDES.map(s => [s, []]));

  for (const item of rawItems) {
    if (!item || item.kind !== 'busy') continue;
    if (item.sourceStatus === 'API_ERROR' || item.error === true) continue; // Saltar errores

    if (item.sede && groups[item.sede]) {
      (item.busy || []).forEach(slot => {
        const clipped = cropToWindow(slot, windowStartISO, windowEndISO);
        if (clipped) groups[item.sede].push(clipped);
      });
    }
    else if (item.busyBySede) {
      for (const [s, list] of Object.entries(item.busyBySede)) {
        if (groups[s] && Array.isArray(list)) {
          list.forEach(slot => {
            const clipped = cropToWindow(slot, windowStartISO, windowEndISO);
            if (clipped) groups[s].push(clipped);
          });
        }
      }
    }
  }

  const busyBySede = Object.fromEntries(SEDES.map(s => [s, mergeExclusive(groups[s])]));

  // Obtener Allowed Slots desde SCHEDULE_GATE
  let allowedSlots = [];
  let scheduleGateError = null;

  try {
    const gate = $('SCHEDULE_GATE').first()?.json;
    if (gate?.allowedSlots && Array.isArray(gate.allowedSlots)) {
      allowedSlots = gate.allowedSlots;
    } else if (gate?.error) {
      scheduleGateError = gate.error;
    }
  } catch (err) {
    scheduleGateError = err.message;
    log('WARN', 'Error cargando SCHEDULE_GATE', { error: err.message });
  }

  const preFilterCount = allowedSlots.length;

  // Filtrar solo sedes vÃ¡lidas Y sin error de API
  allowedSlots = allowedSlots.filter(s => {
    if (!s || !SEDES.includes(s.sede)) return false;
    if (sedesConError.has(s.sede)) return false; // No mostrar horarios de sedes con error
    return true;
  });

  // Aplicar filtro de ocupaciÃ³n
  let finalSlots = filterBusySlots(allowedSlots, busyBySede);

  // Aplicar lÃ­mite
  const maxResults = ctx.maxResults || LIMITS.MAX_TOTAL_SLOTS;
  const wasLimited = finalSlots.length > maxResults;
  finalSlots = finalSlots.slice(0, maxResults);

  const busyBySedeLocal = Object.fromEntries(
    SEDES.map(s => [s, (busyBySede[s] || []).map(iv => ({ 
      ...iv, 
      startLocal: formatLocal(iv.start, TZ), 
      endLocal: formatLocal(iv.end, TZ) 
    }))])
  );

  const result = {
    kind: 'busy_combined',
    build: { version: VERSION, generatedAt: new Date().toISOString() },
    requestId,
    ctx,

    scheduleGate: {
      allowedSlots: finalSlots 
    },

    busyBySede,
    busyBySedeLocal,

    // â•â•â• WARNINGS PARA BUILD_RESPONSE â•â•â•
    warnings: {
      sedesConError: [...sedesConError],
      apiErrors: apiErrors.length > 0 ? apiErrors : null,
      wasLimited,
      scheduleGateError
    },

    metrics: {
      totalInputSlots: preFilterCount,
      totalBusyBlocks: Object.values(busyBySede).flat().length,
      filteredByBusy: allowedSlots.length - finalSlots.length,
      filteredByApiError: preFilterCount - allowedSlots.length,
      finalOutput: finalSlots.length,
      ctxSource,
      executionMs: Date.now() - startTime
    },

    debugLocal: { tz: TZ, sedes: SEDES },
    _logs: DEBUG ? LOGS : undefined
  };

  log('SUCCESS', `Finalizado. Slots entregados: ${finalSlots.length}`, { 
    sedesConError: [...sedesConError],
    warnings: result.warnings 
  });

  return [{
    json: result,
    pairedItem: $input.all().map((_, i) => ({ item: i }))
  }];

} catch (err) {
  log('ERROR', err.message, { stack: err.stack });
  return [{ 
    json: { 
      kind: 'busy_combined',
      build: { version: VERSION },
      scheduleGate: { allowedSlots: [] },
      busyBySede: {},
      metrics: { error: err.message },
      error: 'COMBINE_FAIL', 
      errorMessage: 'Error interno al procesar disponibilidad. Por favor intenta de nuevo.',
      _logs: LOGS
    } 
  }];
}

'use strict';

/**
 * â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
 * CALC_AVAILABILITY v13.0.0 â€” ROBUST + TIME FILTERS
 * â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
 * 
 * CAMBIOS v13.0:
 * âœ… FIX #1: Soporte para onlyMorning / onlyAfternoon
 * âœ… FIX #2: LÃ­mite configurable (no hardcoded)
 * âœ… FIX #3: PropagaciÃ³n de warnings desde Combine Busy
 * âœ… FIX #4: Mejor enriquecimiento de slots
 * 
 * â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
 */

const VERSION = '13.0.0-ROBUST';
const DEFAULT_TZ = 'America/Mexico_City';
const DEFAULT_LIMIT = 100; // Aumentado de 50

function getLocalTime(iso, tz) {
  try {
    return new Intl.DateTimeFormat('es-MX', {
      timeZone: tz, hour: '2-digit', minute: '2-digit', hour12: false
    }).format(new Date(iso));
  } catch (e) { 
    return iso?.split('T')[1]?.substring(0, 5) || '00:00'; 
  }
}

function getLocalHour(iso, tz) {
  try {
    const time = getLocalTime(iso, tz);
    return parseInt(time.split(':')[0]) || 0;
  } catch {
    return 12;
  }
}

function getDateKey(iso) {
  return iso?.split('T')[0] || '';
}

function getWeekdayName(iso, tz) {
  try {
    return new Intl.DateTimeFormat('es-MX', {
      timeZone: tz, weekday: 'long'
    }).format(new Date(iso));
  } catch {
    return '';
  }
}

function formatDateSpanish(iso, tz) {
  try {
    return new Intl.DateTimeFormat('es-MX', {
      timeZone: tz,
      weekday: 'long',
      day: 'numeric',
      month: 'long'
    }).format(new Date(iso));
  } catch {
    return iso?.split('T')[0] || '';
  }
}

try {
  const startTime = Date.now();

  const items = $input.all();
  const inputData = items[0]?.json || {};

  // â•â•â• CARGAR CONTEXTO â•â•â•
  let rawSlots = [];
  let requestId = inputData.requestId || `calc-${Date.now()}`;
  let TZ = DEFAULT_TZ;
  let ctx = {};
  let warnings = {};

  if (inputData.kind === 'busy_combined') {
    rawSlots = inputData.scheduleGate?.allowedSlots || [];
    TZ = inputData.ctx?.tz || inputData.debugLocal?.tz || DEFAULT_TZ;
    ctx = inputData.ctx || {};
    warnings = inputData.warnings || {};
  } 
  else if (inputData.allowedSlots) {
    rawSlots = inputData.allowedSlots;
    TZ = inputData.tz || DEFAULT_TZ;
    ctx = inputData;
  }
  else if (inputData.scheduleGate?.allowedSlots) {
    rawSlots = inputData.scheduleGate.allowedSlots;
    TZ = inputData.tz || DEFAULT_TZ;
    ctx = inputData;
  }
  else if (Array.isArray(inputData)) {
    rawSlots = inputData;
  }

  // â•â•â• PROPAGAR ERRORES â•â•â•
  if (inputData.error) {
    return [{
      json: {
        kind: 'avail',
        version: VERSION,
        requestId,
        tz: TZ,
        allowedSlots: [],
        summary: { totalAvailable: 0, returned: 0, daysWithSlots: 0 },
        error: inputData.error,
        errorMessage: inputData.errorMessage || 'Error en el sistema de calendario',
        warnings,
        timestamp: new Date().toISOString()
      }
    }];
  }

  // â•â•â• SI NO HAY SLOTS â•â•â•
  if (!rawSlots || rawSlots.length === 0) {
    return [{
      json: {
        kind: 'avail',
        version: VERSION,
        requestId,
        tz: TZ,
        ctx,
        allowedSlots: [],
        summary: { totalAvailable: 0, returned: 0, daysWithSlots: 0 },
        warnings,
        timestamp: new Date().toISOString(),
        _note: 'No slots received from previous node'
      }
    }];
  }

  // â•â•â• FILTROS DE TIEMPO (MAÃ‘ANA/TARDE) â•â•â•
  const onlyMorning = ctx.onlyMorning === true;
  const onlyAfternoon = ctx.onlyAfternoon === true;

  let filteredSlots = rawSlots;

  if (onlyMorning || onlyAfternoon) {
    filteredSlots = rawSlots.filter(slot => {
      if (!slot || !slot.start) return false;
      const hour = getLocalHour(slot.start, TZ);

      if (onlyMorning) {
        return hour < 14; // Antes de 2pm = maÃ±ana
      }
      if (onlyAfternoon) {
        return hour >= 14; // 2pm o despuÃ©s = tarde
      }
      return true;
    });
  }

  // â•â•â• ENRIQUECER SLOTS â•â•â•
  const enrichedSlots = filteredSlots.map(slot => {
    if (!slot || !slot.start) return null;

    const localTime = getLocalTime(slot.start, TZ);
    const hour = parseInt(localTime.split(':')[0]) || 0;
    const weekday = getWeekdayName(slot.start, TZ);
    const dateFormatted = formatDateSpanish(slot.start, TZ);

    return {
      ...slot,
      startHM: localTime,
      shiftLabel: hour < 14 ? 'MaÃ±ana' : 'Tarde',
      dayKey: getDateKey(slot.start),
      weekday,
      dateFormatted,
      // Para facilitar ordenamiento
      _sortKey: new Date(slot.start).getTime()
    };
  }).filter(Boolean);

  // â•â•â• ORDENAR CRONOLÃ“GICAMENTE â•â•â•
  enrichedSlots.sort((a, b) => a._sortKey - b._sortKey);

  // â•â•â• APLICAR LÃMITE â•â•â•
  const limit = ctx.maxResults || DEFAULT_LIMIT;
  const wasLimited = enrichedSlots.length > limit;
  const limitedSlots = enrichedSlots.slice(0, limit);

  // Limpiar _sortKey del output
  limitedSlots.forEach(s => delete s._sortKey);

  // â•â•â• GENERAR RESUMEN POR DÃA â•â•â•
  const slotsByDay = {};
  for (const slot of limitedSlots) {
    if (!slotsByDay[slot.dayKey]) {
      slotsByDay[slot.dayKey] = {
        date: slot.dayKey,
        dateFormatted: slot.dateFormatted,
        weekday: slot.weekday,
        slots: [],
        sedes: new Set()
      };
    }
    slotsByDay[slot.dayKey].slots.push(slot);
    slotsByDay[slot.dayKey].sedes.add(slot.sede);
  }

  // Convertir Sets a Arrays
  const dayAnalysis = Object.values(slotsByDay).map(day => ({
    date: day.date,
    dateFormatted: day.dateFormatted,
    weekday: day.weekday,
    slotsCount: day.slots.length,
    sedes: [...day.sedes],
    times: day.slots.map(s => s.startHM)
  }));

  const summary = {
    totalAvailable: rawSlots.length,
    afterTimeFilter: filteredSlots.length,
    returned: limitedSlots.length,
    wasLimited,
    firstSlot: limitedSlots.length > 0 ? limitedSlots[0].start : null,
    lastSlot: limitedSlots.length > 0 ? limitedSlots[limitedSlots.length - 1].start : null,
    daysWithSlots: Object.keys(slotsByDay).length,
    filtersApplied: {
      onlyMorning,
      onlyAfternoon
    }
  };

  // â•â•â• AGREGAR WARNINGS SI HAY â•â•â•
  if (warnings.sedesConError?.length > 0) {
    summary.sedesConError = warnings.sedesConError;
  }

  return [{
    json: {
      kind: 'avail',
      version: VERSION,
      requestId,
      tz: TZ,
      ctx,

      allowedSlots: limitedSlots,
      summary,
      dayAnalysis,

      warnings: Object.keys(warnings).length > 0 ? warnings : undefined,

      timestamp: new Date().toISOString(),
      executionMs: Date.now() - startTime
    }
  }];

} catch (error) {
  return [{
    json: {
      kind: 'avail',
      version: VERSION,
      status: 'error',
      error: error.message,
      allowedSlots: [],
      summary: { totalAvailable: 0 },
      timestamp: new Date().toISOString()
    }
  }];
}

'use strict';

/**
 * â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
 * SCHEDULE_GATE v17.0.0 â€” WEEK A/B VALIDATION + ROBUST
 * â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
 *
 * CAMBIOS v17.0:
 * 
 * âœ… FIX #1: ValidaciÃ³n estricta de anchor_date para semanas A/B
 * âœ… FIX #2: Si anchor_date es NULL, usa horarios sin alternancia (warning)
 * âœ… FIX #3: Mejor normalizaciÃ³n de sede
 * âœ… FIX #4: Logging detallado para debugging
 * âœ… FIX #5: Soporte para onlyMorning/onlyAfternoon
 * 
 * â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
 */

const VERSION = '17.0.0-WEEK-VALIDATION';
const DEBUG = true;

// ==================== LOGGING ====================
const LOGS = [];
function log(level, message, data = {}) {
  const entry = {
    timestamp: new Date().toISOString(),
    level: level.toUpperCase(),
    component: 'SCHEDULE_GATE',
    version: VERSION,
    message,
    ...data,
  };
