}

// ==================== TIMEZONE / DATE HELPERS ====================
let _formattersCache = {};
function getFormatters(tz) {
  if (!_formattersCache[tz]) {
    _formattersCache[tz] = {
      tz,
      DTF_FULL: new Intl.DateTimeFormat('en-CA', {
        timeZone: tz,
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
        hour12: false,
      }),
      DTF_DISPLAY: new Intl.DateTimeFormat('es-MX', {
        timeZone: tz,
        weekday: 'long',
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit',
        hour12: false,
      }),
      DTF_HM: new Intl.DateTimeFormat('en-US', {
        timeZone: tz,
        hour: '2-digit',
        minute: '2-digit',
        hour12: false,
      }),
    };
  }
  return _formattersCache[tz];
}

function getLocalParts(date, DTF_FULL) {
  const parts = DTF_FULL.formatToParts(date);
  const obj = {};
  for (const p of parts) obj[p.type] = p.value;
  return {
    y: +obj.year,
    m: +obj.month,
    d: +obj.day,
    hh: +obj.hour,
    mm: +obj.minute,
    ss: +obj.second,
  };
}

function localToUTC(y, m, d, hh = 0, mm = 0, ss = 0, DTF_FULL) {
  const naive = Date.UTC(y, m - 1, d, hh, mm, ss);
  const asLocal = getLocalParts(new Date(naive), DTF_FULL);
  const localEpoch = Date.UTC(
    asLocal.y,
    asLocal.m - 1,
    asLocal.d,
    asLocal.hh,
    asLocal.mm,
    asLocal.ss,
  );
  return naive - (localEpoch - naive);
}

function timeAtDay(date, hhmm, DTF_FULL) {
  const [hh, mm] = hhmm.split(':').map(Number);
  const parts = getLocalParts(date, DTF_FULL);
  return localToUTC(parts.y, parts.m, parts.d, hh, mm, 0, DTF_FULL);
}

function getDOW(date, DTF_FULL) {
  const parts = getLocalParts(date, DTF_FULL);
  const d = new Date(Date.UTC(parts.y, parts.m - 1, parts.d, 12));
  return d.getUTCDay();
}

// ==================== WEEK A/B LOGIC ====================
function getWeekType(currentDate, anchorDate, anchorWeekType, DTF_FULL) {
  if (!anchorDate || !anchorWeekType) {
    return null; // Sin alternancia
  }

  try {
    const anchor = new Date(anchorDate);
    const current = new Date(currentDate);

    // Calcular diferencia en semanas
    const msPerWeek = 7 * 24 * 60 * 60 * 1000;
    const diffMs = current.getTime() - anchor.getTime();
    const diffWeeks = Math.floor(diffMs / msPerWeek);

    // Si diffWeeks es par, misma semana que anchor
    // Si diffWeeks es impar, semana opuesta
    const isEven = diffWeeks % 2 === 0;

    if (isEven) {
      return anchorWeekType; // 'A' o 'B'
    } else {
      return anchorWeekType === 'A' ? 'B' : 'A';
    }
  } catch (err) {
    log('WARN', 'Error calculando weekType', { error: err.message });
    return null;
  }
}

// ==================== MAIN ====================
try {
  const startTime = Date.now();

  // Cargar contexto
  let ctx = {};
  try {
    ctx = $('SET_DEFAULTS1').first()?.json || {};
  } catch {
    log('WARN', 'No se pudo cargar SET_DEFAULTS');
  }

  const TZ = ctx.tz || 'America/Mexico_City';
  const { DTF_FULL } = getFormatters(TZ);

  // Cargar SCHEDULE desde static data o CARGAR_HORARIOS
  let SCHEDULE = null;
  try {
    const store = $getWorkflowStaticData('global');
    if (store?.SCHEDULE) {
      SCHEDULE = store.SCHEDULE;
    }
  } catch {}

  if (!SCHEDULE) {
    try {
      const horarios = $('CARGAR_HORARIOS1').first()?.json;
      if (horarios?.SCHEDULE) {
        SCHEDULE = horarios.SCHEDULE;
        $getWorkflowStaticData('global').SCHEDULE = SCHEDULE;
      }
    } catch {}
  }

  if (!SCHEDULE || !SCHEDULE.sedes) {
    log('ERROR', 'No se pudo cargar SCHEDULE');
    return [{ json: { error: 'NO_SCHEDULE', allowedSlots: [], _logs: LOGS } }];
  }

  const SEDES = Object.keys(SCHEDULE.sedes);

  // Filtrar sedes si hay preferencia
  let targetSedes = SEDES;
  if (ctx.sedePreferida && SEDES.includes(ctx.sedePreferida)) {
    targetSedes = [ctx.sedePreferida];
    log('INFO', `Filtrando por sede preferida: ${ctx.sedePreferida}`);
  }

  // Validar anchor_date para semanas A/B
  const weekValidation = {};
  for (const sede of SEDES) {
    const meta = SCHEDULE.meta?.[sede] || {};
    if (meta.hasWeekAlternation) {
      if (!meta.anchorDate || !meta.anchorWeekType) {
        weekValidation[sede] = {
          valid: false,
          warning: `Sede ${sede} tiene alternancia A/B pero anchor_date no estÃ¡ configurado`
        };
        log('WARN', weekValidation[sede].warning);
      } else {
        weekValidation[sede] = { valid: true };
      }
    }
  }

  // Generar slots
  const allowedSlots = [];
  const windowStart = new Date(ctx.windowStart);
  const windowEnd = new Date(ctx.windowEnd);
  const slotMinutes = ctx.slotMinutes || 30;
  const shortNoticeMs = (ctx.shortNoticeMin || 120) * 60 * 1000;
  const now = new Date();
  const minBookableTime = now.getTime() + shortNoticeMs;

  let currentDate = new Date(windowStart);
  let daysProcessed = 0;
  const maxDays = 30;

  while (currentDate <= windowEnd && daysProcessed < maxDays) {
    const dow = getDOW(currentDate, DTF_FULL);

    for (const sede of targetSedes) {
      const sedeSchedule = SCHEDULE.sedes[sede];
      if (!sedeSchedule) continue;

      // Determinar tipo de semana si aplica
      let weekType = null;
      const meta = SCHEDULE.meta?.[sede] || {};

      if (meta.hasWeekAlternation) {
        if (meta.anchorDate && meta.anchorWeekType) {
          weekType = getWeekType(currentDate, meta.anchorDate, meta.anchorWeekType, DTF_FULL);
          log('DEBUG', `Semana ${weekType} para ${sede} el ${currentDate.toISOString().split('T')[0]}`);
        } else {
          // Sin anchor_date, usar todos los horarios (fallback)
          log('WARN', `${sede}: usando horarios sin alternancia por falta de anchor_date`);
        }
      }

      // Obtener turnos del dÃ­a
      const dayConfig = sedeSchedule[dow];
      if (!dayConfig) continue;

      let shifts = [];

      if (Array.isArray(dayConfig)) {
        // Formato simple: [["09:00", "14:00", "MaÃ±ana"], ...]
        shifts = dayConfig;
      } else if (typeof dayConfig === 'object') {
        // Formato con semanas: { A: [...], B: [...] }
        if (weekType && dayConfig[weekType]) {
          shifts = dayConfig[weekType];
        } else {
          // Sin weekType definido, tomar todos (fallback)
          shifts = [...(dayConfig.A || []), ...(dayConfig.B || [])];
        }
      }

      // Generar slots para cada turno
      for (const shift of shifts) {
        if (!Array.isArray(shift) || shift.length < 2) continue;

        const [startHM, endHM, label] = shift;

        // Filtro por maÃ±ana/tarde si estÃ¡ activo
        if (ctx.onlyMorning) {
          const startHour = parseInt(startHM.split(':')[0]);
          if (startHour >= 14) continue; // Saltar turnos de tarde
        }
        if (ctx.onlyAfternoon) {
          const startHour = parseInt(startHM.split(':')[0]);
          if (startHour < 14) continue; // Saltar turnos de maÃ±ana
        }

        const shiftStartMs = timeAtDay(currentDate, startHM, DTF_FULL);
        const shiftEndMs = timeAtDay(currentDate, endHM, DTF_FULL);

        let slotStartMs = shiftStartMs;
        const slotDurationMs = slotMinutes * 60 * 1000;

        while (slotStartMs + slotDurationMs <= shiftEndMs) {
          // Verificar que no sea muy pronto (short notice)
          if (slotStartMs >= minBookableTime) {
            const slotEndMs = slotStartMs + slotDurationMs;

            allowedSlots.push({
              id: `${sede}-${slotStartMs}`,
              sede,
              start: new Date(slotStartMs).toISOString(),
              end: new Date(slotEndMs).toISOString(),
              shiftLabel: label || (parseInt(startHM.split(':')[0]) < 14 ? 'MaÃ±ana' : 'Tarde'),
              weekType: weekType || undefined
            });
          }

          slotStartMs += slotDurationMs;
        }
      }
    }

    // Avanzar al siguiente dÃ­a
    currentDate.setDate(currentDate.getDate() + 1);
    daysProcessed++;
  }

  log('SUCCESS', `Generados ${allowedSlots.length} slots`, {
    sedes: targetSedes,
    daysProcessed
  });

  return [{
    json: {
      kind: 'schedule_gate',
      version: VERSION,
      allowedSlots,
      weekValidation,
      ctx: {
        sedePreferida: ctx.sedePreferida,
        onlyMorning: ctx.onlyMorning,
        onlyAfternoon: ctx.onlyAfternoon,
        shortNoticeMin: ctx.shortNoticeMin
      },
      metrics: {
        totalSlots: allowedSlots.length,
        daysProcessed,
        sedes: targetSedes,
        executionMs: Date.now() - startTime
      },
      _logs: DEBUG ? LOGS : undefined
    }
  }];

} catch (err) {
  log('ERROR', err.message, { stack: err.stack });
  return [{
    json: {
      error: 'SCHEDULE_GATE_FAIL',
      errorMessage: err.message,
      allowedSlots: [],
      _logs: LOGS
    }
  }];
}

'use strict';

/**
 * â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
 * NORMALIZE_BUSY v3.0.0 â€” API ERROR DETECTION
 * â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
 * 
 * Sede: POLANCO
 * 
 * CAMBIOS v3.0:
 * âœ… FIX CRÃTICO: Detecta errores de Google Calendar y los propaga
 * âœ… NO asume "calendario libre" cuando hay error de API
 * âœ… Mejor logging para debugging
 * 
 * â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
 */

const SEDE_OBJETIVO = 'POLANCO';
const VERSION = '3.0.0-ERROR-DETECTION';
const DEFAULT_TZ = 'America/Mexico_City';

const toMs = (v) => {
  try {
    return new Date(v).getTime();
  } catch {
    return 0;
  }
};

const toISO = (ms) => {
  try {
    return new Date(ms).toISOString();
  } catch {
    return null;
  }
};

try {
  const items = $input.all();
  const rawPairs = [];
  let sourceInfo = { type: 'unknown', calendarIds: [] };
  let apiError = null;

  // 1. Verificar si hubo error de API
  for (const item of items) {
    const json = item.json;

    if (!json) continue;

    // â•â•â• DETECCIÃ“N DE ERRORES DE GOOGLE CALENDAR â•â•â•
    // Caso 1: Error explÃ­cito
    if (json.error) {
      apiError = {
        type: 'GOOGLE_API_ERROR',
        message: json.error?.message || json.error || 'Unknown error',
        code: json.error?.code || json.code,
        status: json.error?.status
      };
      sourceInfo.type = 'google_error';
      console.log(`[ERROR] ${SEDE_OBJETIVO}: Google Calendar error:`, JSON.stringify(apiError));
      continue;
    }

    // Caso 2: Respuesta vacÃ­a sospechosa (puede ser error silencioso)
    if (json.code && json.code >= 400) {
      apiError = {
        type: 'HTTP_ERROR',
        message: json.message || `HTTP ${json.code}`,
        code: json.code
      };
      sourceInfo.type = 'http_error';
      continue;
    }

    // Caso 3: continueOnFail puede pasar objetos vacÃ­os
    if (Object.keys(json).length === 0) {
      console.log(`[WARN] ${SEDE_OBJETIVO}: Objeto vacÃ­o recibido (posible error silencioso)`);
      // No lo tratamos como error fatal, pero lo logueamos
    }

    // â•â•â• PROCESAR DATOS VÃLIDOS â•â•â•

    // Estructura Raw de Google Calendar FreeBusy
    if (json.calendars) {
      sourceInfo.type = 'google_raw';
      for (const calId of Object.keys(json.calendars)) {
        sourceInfo.calendarIds.push(calId);

        // Verificar errores por calendario
        const calData = json.calendars[calId];
        if (calData.errors && calData.errors.length > 0) {
          apiError = {
            type: 'CALENDAR_SPECIFIC_ERROR',
            message: calData.errors[0]?.reason || 'Calendar error',
            calendarId: calId
          };
          continue;
        }

        const busyList = calData?.busy || [];
        for (const slot of busyList) {
          if (slot.start && slot.end) {
            const startMs = toMs(slot.start);
            const endMs = toMs(slot.end);
            if (startMs > 0 && endMs > startMs) {
              rawPairs.push([startMs, endMs]);
            }
          }
        }
      }
    }
    // Estructura simple
    else if (Array.isArray(json.busy)) {
      sourceInfo.type = 'simple_array';
      for (const slot of json.busy) {
        if (slot.start && slot.end) {
          const startMs = toMs(slot.start);
          const endMs = toMs(slot.end);
          if (startMs > 0 && endMs > startMs) {
            rawPairs.push([startMs, endMs]);
          }
        }
      }
    }
    // FreeBusy response vacÃ­a (calendario realmente libre)
    else if (json.timeMin && json.timeMax && !json.error) {
      sourceInfo.type = 'freebusy_empty';
      // OK - calendario libre
    }
  }

  // â•â•â• SI HUBO ERROR DE API, PROPAGAR â•â•â•
  if (apiError) {
    console.log(`[CRITICAL] ${SEDE_OBJETIVO}: Propagando error de API - NO asumir calendario libre`);
    return [{
      json: {
        kind: 'busy',
        sede: SEDE_OBJETIVO,
        busy: [],
        busyBySede: { [SEDE_OBJETIVO]: [] },
        sourceStatus: 'API_ERROR',
        error: true,
        errorMessage: apiError.message,
        errorDetails: apiError,
        version: VERSION,
        timestamp: new Date().toISOString()
      }
    }];
  }

  // 2. Merge overlapping intervals
  rawPairs.sort((a, b) => a[0] - b[0]);
  const merged = [];

  if (rawPairs.length > 0) {
    let [currStart, currEnd] = rawPairs[0];

    for (let i = 1; i < rawPairs.length; i++) {
      const [nextStart, nextEnd] = rawPairs[i];
      if (nextStart <= currEnd) {
        currEnd = Math.max(currEnd, nextEnd);
      } else {
        const startISO = toISO(currStart);
        const endISO = toISO(currEnd);
        if (startISO && endISO) {
          merged.push({ sede: SEDE_OBJETIVO, start: startISO, end: endISO });
        }
        currStart = nextStart;
        currEnd = nextEnd;
      }
    }

    // Ãšltimo intervalo
    const startISO = toISO(currStart);
    const endISO = toISO(currEnd);
    if (startISO && endISO) {
      merged.push({ sede: SEDE_OBJETIVO, start: startISO, end: endISO });
    }
  }

  // 3. Output exitoso
  return [{
    json: {
      kind: 'busy',
      sede: SEDE_OBJETIVO,
      busy: merged,
      busyBySede: { [SEDE_OBJETIVO]: merged },
      sourceStatus: 'OK',
      sourceInfo,
      version: VERSION,
      metrics: {
        rawIntervals: rawPairs.length,
        mergedIntervals: merged.length
      },
      timestamp: new Date().toISOString()
    }
  }];

} catch (err) {
  console.log(`[ERROR] ${SEDE_OBJETIVO}: Exception in Normalize:`, err.message);
  return [{
    json: {
      kind: 'busy',
      sede: SEDE_OBJETIVO,
      busy: [],
      busyBySede: { [SEDE_OBJETIVO]: [] },
      sourceStatus: 'API_ERROR',
      error: true,
      errorMessage: `Exception: ${err.message}`,
      version: VERSION,
      timestamp: new Date().toISOString()
    }
  }];
}

