const isHHMM = (s) => {
  if (typeof s !== 'string') return false;
  const m = /^(\d{2}):(\d{2})$/.exec(s);
  if (!m) return false;
  const hh = +m[1];
  const mm = +m[2];
  return hh >= 0 && hh <= 23 && mm >= 0 && mm <= 59;
};

const validateShift = (shift, sede, dow, ctx = {}) => {
  if (!Array.isArray(shift) || shift.length < 2) {
    log('error', 'Shift invÃ¡lido', { sede, dow, shift, ctx });
    return false;
  }
  const [start, end, label] = shift;
  if (!isHHMM(start) || !isHHMM(end)) return false;

  const toMin = (s) => {
    const [h, m] = s.split(':').map(Number);
    return h * 60 + m;
  };

  return toMin(start) < toMin(end);
};

function detectWeekAlternation(horario) {
  // Detecta si el horario usa semanas A/B
  for (const dow of Object.keys(horario)) {
    const dayConfig = horario[dow];
    if (dayConfig && typeof dayConfig === 'object' && !Array.isArray(dayConfig)) {
      if (dayConfig.A || dayConfig.B) {
        return true;
      }
    }
  }
  return false;
}

try {
  // Obtener parÃ¡metros del trigger
  let triggerParams = {};
  try {
    const trigger = $('When Executed by Another Workflow').first();
    if (trigger) triggerParams = trigger.json || {};
  } catch {}

  // Obtener datos de sedes desde Postgres
  const sedesData = $input.all().map(item => item.json);

  if (!sedesData || sedesData.length === 0) {
    log('ERROR', 'No se encontraron sedes en la base de datos');
    return [{
      json: {
        error: 'NO_SEDES_FOUND',
        errorMessage: 'No se encontraron horarios configurados',
        triggerParams,
        _logs: LOGS
      }
    }];
  }

  // Construir estructura SCHEDULE
  const SCHEDULE = {
    tz: DEFAULT_TZ,
    version: VERSION,
    sedes: {},
    meta: {},
    sedeInfo: {}
  };

  const warnings = [];

  for (const row of sedesData) {
    const sede = (row.sede || '').toUpperCase().trim();
    if (!sede) continue;

    // Parsear horario_json
    let horario = {};
    try {
      if (typeof row.horario_json === 'string') {
        horario = JSON.parse(row.horario_json);
      } else if (typeof row.horario_json === 'object') {
        horario = row.horario_json;
      }
    } catch (err) {
      log('ERROR', `Error parseando horario_json para ${sede}`, { error: err.message });
      continue;
    }

    SCHEDULE.sedes[sede] = horario;

    // â•â•â• VALIDACIÃ“N DE SEMANAS A/B â•â•â•
    const hasWeekAlternation = detectWeekAlternation(horario);
    const hasAnchorDate = !!row.anchor_date;
    const hasAnchorWeekType = !!row.anchor_week_type;

    if (hasWeekAlternation && (!hasAnchorDate || !hasAnchorWeekType)) {
      const warning = {
        sede,
        issue: 'MISSING_ANCHOR_CONFIG',
        message: `Sede ${sede} tiene horarios A/B pero falta anchor_date (${hasAnchorDate}) o anchor_week_type (${hasAnchorWeekType})`,
        impact: 'Se mostrarÃ¡n horarios de AMBAS semanas (fallback)'
      };
      warnings.push(warning);
      log('WARN', warning.message);
    }

    // Metadatos de la sede
    SCHEDULE.meta[sede] = {
      hasWeekAlternation,
      anchorDate: row.anchor_date || null,
      anchorWeekType: row.anchor_week_type || null,
      anchorValid: hasWeekAlternation ? (hasAnchorDate && hasAnchorWeekType) : true,
      calendarId: row.calendar_id,
      workingDays: Object.keys(horario).map(k => parseInt(k)).filter(k => !isNaN(k))
    };

    // Info de sede para respuestas
    SCHEDULE.sedeInfo[sede] = {
      displayName: row.display_name || sede,
      direccion: row.direccion || '',
      mapsUrl: row.maps_url || '',
      calendarId: row.calendar_id
    };
  }

  // Guardar en static data para uso global
  try {
    $getWorkflowStaticData('global').SCHEDULE = SCHEDULE;
  } catch (err) {
    log('WARN', 'No se pudo guardar SCHEDULE en static data', { error: err.message });
  }

  log('SUCCESS', `SCHEDULE cargado: ${Object.keys(SCHEDULE.sedes).length} sedes`, {
    sedes: Object.keys(SCHEDULE.sedes),
    warnings: warnings.length
  });

  return [{
    json: {
      SCHEDULE,
      triggerParams,
      warnings: warnings.length > 0 ? warnings : undefined,
      version: VERSION,
      _logs: DEBUG ? LOGS : undefined
    }
  }];

} catch (err) {
  log('ERROR', err.message, { stack: err.stack });
  return [{
    json: {
      error: 'CARGAR_HORARIOS_FAIL',
      errorMessage: err.message,
      _logs: LOGS
    }
  }];
}

