/* ===================== UTILIDADES DE FECHA ===================== */

function getLocalParts(date, tz) {
  try {
    const formatter = new Intl.DateTimeFormat('en-CA', {
      timeZone: tz,
      year: 'numeric', month: '2-digit', day: '2-digit',
      hour: '2-digit', minute: '2-digit', hour12: false
    });
    const parts = formatter.formatToParts(date);
    const obj = {};
    for (const p of parts) obj[p.type] = p.value;
    return { y: +obj.year, m: +obj.month, d: +obj.day, hh: +obj.hour, mm: +obj.minute };
  } catch (e) {
    const d = new Date(date);
    return { y: d.getFullYear(), m: d.getMonth() + 1, d: d.getDate(), hh: d.getHours(), mm: d.getMinutes() };
  }
}

function getTodayYMD(tz) {
  const p = getLocalParts(new Date(), tz);
  return `${p.y}-${String(p.m).padStart(2, '0')}-${String(p.d).padStart(2, '0')}`;
}

function getLocalHour(tz) {
  const p = getLocalParts(new Date(), tz);
  return p.hh;
}

function addDaysToYMD(ymd, days) {
  const [y, m, d] = ymd.split('-').map(Number);
  const date = new Date(Date.UTC(y, m - 1, d, 12, 0, 0));
  date.setUTCDate(date.getUTCDate() + days);
  return `${date.getUTCFullYear()}-${String(date.getUTCMonth() + 1).padStart(2, '0')}-${String(date.getUTCDate()).padStart(2, '0')}`;
}

function getLocalDOW(tz) {
  const p = getLocalParts(new Date(), tz);
  const d = new Date(p.y, p.m - 1, p.d);
  return d.getDay(); // 0=Dom, 6=Sab
}

/* ===================== NORMALIZACIÃ“N ===================== */

function normalizeIntent(rawIntent) {
  if (!rawIntent || typeof rawIntent !== 'string') return 'today';

  let intent = rawIntent.toLowerCase().trim();

  // Quitar caracteres extraÃ±os
  intent = intent.replace(/[\n\r\t]/g, '').trim();

  // Buscar en aliases directos
  if (CONFIG.INTENT_ALIASES[intent]) {
    return CONFIG.INTENT_ALIASES[intent];
  }

  // Buscar dÃ­a de la semana
  for (const [dia, dow] of Object.entries(CONFIG.DIAS_SEMANA)) {
    if (intent.includes(dia)) {
      return dia; // Retornar el dÃ­a encontrado
    }
  }

  // Si no se reconoce, retornar como estÃ¡ (puede ser "specific" u otro)
  return intent;
}

function normalizeSede(input) {
  if (!input || typeof input !== 'string') return null;

  // Limpiar: quitar acentos, espacios extras, caracteres raros
  let cleaned = input
    .toUpperCase()
    .normalize('NFD').replace(/[\u0300-\u036f]/g, '') // Quitar acentos
    .replace(/[\n\r\t]/g, '')
    .replace(/[^A-Z\s]/g, '')
    .trim();

  // Buscar en aliases
  if (CONFIG.SEDE_ALIASES[cleaned]) return CONFIG.SEDE_ALIASES[cleaned];

  // Buscar coincidencia parcial
  for (const [alias, sede] of Object.entries(CONFIG.SEDE_ALIASES)) {
    if (cleaned.includes(alias) || alias.includes(cleaned)) {
      return sede;
    }
  }

  return CONFIG.VALID_SEDES.includes(cleaned) ? cleaned : null;
}

/* ===================== LÃ“GICA PRINCIPAL ===================== */

try {
  const TZ = CONFIG.TZ;
  const LOGS = [];

  // 1. Recopilar Inputs
  let params = {};

  for (const nodeName of CONFIG.TRIGGER_NODE_NAMES) {
    try {
      const triggerNode = $(nodeName).first();
      if (triggerNode?.json) params = { ...params, ...triggerNode.json };
    } catch {}
  }

  try {
    const input = $input.first()?.json;
    if (input) params = { ...params, ...input };
  } catch {}

  // 2. Extraer y normalizar variables
  const rawDateIntent = params.dateIntent || 'today';
  const dateIntent = normalizeIntent(rawDateIntent);
  const specificDate = (params.specificDate || '').trim();
  const rawSede = params.sedePreferida || '';
  const sedePreferida = normalizeSede(rawSede);

  // Booleanos robustos
  const onlyMorning = params.onlyMorning === true || params.onlyMorning === 'true';
  const onlyAfternoon = params.onlyAfternoon === true || params.onlyAfternoon === 'true';

  LOGS.push(`Raw dateIntent: "${rawDateIntent}" â†’ Normalized: "${dateIntent}"`);
  LOGS.push(`Raw sede: "${rawSede}" â†’ Normalized: "${sedePreferida}"`);

  // 3. Calcular Ventana de Tiempo
  const todayYMD = getTodayYMD(TZ);
  const currentHour = getLocalHour(TZ);
  const currentDOW = getLocalDOW(TZ);

  let startYMD = todayYMD;
  let endYMD = todayYMD;
  let isToday = false;
  let isTomorrow = false;
  let expandedDueToLateNight = false;

  switch (dateIntent) {
    case 'tomorrow':
      startYMD = addDaysToYMD(todayYMD, 1);
      endYMD = startYMD;
      isTomorrow = true;
      break;

    case 'day_after_tomorrow':
      startYMD = addDaysToYMD(todayYMD, 2);
      endYMD = startYMD;
      break;

    case 'today':
      startYMD = todayYMD;
      endYMD = todayYMD;
      isToday = true;

      // ğŸ”¥ FIX: Si es muy tarde (despuÃ©s de las 10 PM), expandir a maÃ±ana
      if (currentHour >= CONFIG.LATE_NIGHT_HOUR) {
        endYMD = addDaysToYMD(todayYMD, 1);
        expandedDueToLateNight = true;
        LOGS.push(`âš ï¸ Late night (${currentHour}h) - expandiendo ventana a maÃ±ana`);
      }
      break;

    case 'specific':
      if (specificDate && /^\d{4}-\d{2}-\d{2}$/.test(specificDate)) {
        startYMD = specificDate;
        endYMD = specificDate;
        isToday = (specificDate === todayYMD);
        isTomorrow = (specificDate === addDaysToYMD(todayYMD, 1));
      } else {
        // Fecha invÃ¡lida, usar hoy
        LOGS.push(`âš ï¸ specificDate invÃ¡lido: "${specificDate}", usando hoy`);
      }
      break;

    case 'this_week':
      // Desde hoy hasta el domingo
      const daysToSunday = (7 - currentDOW) % 7 || 7;
      endYMD = addDaysToYMD(todayYMD, daysToSunday);
      break;

    case 'next_week':
      // PrÃ³ximo lunes a domingo
      const daysToNextMon = (8 - currentDOW) % 7 || 7;
      startYMD = addDaysToYMD(todayYMD, daysToNextMon);
      endYMD = addDaysToYMD(startYMD, 6);
      break;

    default:
      // Buscar si es un dÃ­a de la semana
      let targetDOW = CONFIG.DIAS_SEMANA[dateIntent];

      if (targetDOW !== undefined) {
        // Calcular dÃ­as hasta ese dÃ­a
        let daysAdd = (targetDOW - currentDOW + 7) % 7;

        // Si es hoy y ya es tarde, o si daysAdd=0, ir al prÃ³ximo
        if (daysAdd === 0) {
          if (currentHour >= CONFIG.LATE_NIGHT_HOUR) {
            daysAdd = 7; // PrÃ³xima semana
          }
          // Si no es tarde, daysAdd=0 significa hoy (el mismo dÃ­a)
        }

        startYMD = addDaysToYMD(todayYMD, daysAdd);
        endYMD = startYMD;
        isToday = (daysAdd === 0);
        LOGS.push(`DÃ­a "${dateIntent}" â†’ DOW ${targetDOW} â†’ +${daysAdd} dÃ­as â†’ ${startYMD}`);
      } else {
        // No reconocido, usar prÃ³ximos 14 dÃ­as
        LOGS.push(`âš ï¸ dateIntent no reconocido: "${dateIntent}", usando 14 dÃ­as`);
        endYMD = addDaysToYMD(todayYMD, CONFIG.DEFAULT_WINDOW_DAYS);
      }
  }

  // 4. Construir ISO con Offset Fijo de CDMX
  const isoStart = `${startYMD}T00:00:00.000${CONFIG.ISO_OFFSET}`;
  const isoEnd = `${endYMD}T23:59:59.999${CONFIG.ISO_OFFSET}`;

  // Log final
  LOGS.push(`Window: ${isoStart} â†’ ${isoEnd}`);
  console.log(`[SET_DEFAULTS v${VERSION}]\n${LOGS.join('\n')}`);

  return [{
    json: {
      requestId: `disp-${Date.now()}`,
      version: VERSION,
      tz: TZ,

      // Fechas corregidas
      windowStart: isoStart,
      windowEnd: isoEnd,

      // Metadatos
      dateIntent: dateIntent,
      dateIntentOriginal: rawDateIntent,
      specificDate: specificDate || null,
      isToday,
      isTomorrow,
      expandedDueToLateNight,

      // Filtros
      sedePreferida,
      sedes: sedePreferida ? [sedePreferida] : CONFIG.VALID_SEDES,
      onlyMorning,
      onlyAfternoon,

      // ConfiguraciÃ³n
      slotMinutes: 30,
      shortNoticeMin: 120,
      maxResults: 50,

      // Debug
      _debug: {
        currentHour,
        currentDOW,
        todayYMD,
        logs: LOGS
      },

      timestamp: new Date().toISOString()
    }
  }];

} catch (err) {
  console.error(`[SET_DEFAULTS ERROR] ${err.message}`);
  return [{
    json: {
      error: 'SET_DEFAULTS_CRITICAL_FAIL',
      errorMessage: err.message,
      version: VERSION,
      windowStart: new Date().toISOString(),
      windowEnd: new Date(Date.now() + 86400000 * 7).toISOString(),
      tz: 'America/Mexico_City',
      sedes: ['POLANCO', 'SATELITE'],
      slotMinutes: 30,
