    SUNDAY_OFF: "Hoy domingo descansamos en ambas sedes. ğŸ˜Š Pero puedo buscarte los horarios mÃ¡s prÃ³ximos para iniciar la semana. Â¿Te parece bien?",

    // Caso: Piden una sede en un dÃ­a que no abre (ej. SatÃ©lite en Lunes)
    // {sede} se reemplaza por el nombre, {dias} por sus dÃ­as laborales
    WRONG_DAY_SEDE: "En *{sede}* las consultas son los *{dias}*. Para hoy no hay servicio ahÃ­, pero Â¿te gustarÃ­a ver disponibilidad para el prÃ³ximo dÃ­a abierto?",

    // Caso: La sede SÃ abre hoy, pero ya no hay cupo o es muy tarde (regla 2 horas)
    NO_SLOTS_TODAY: "Para hoy la agenda en *{sede}* ya estÃ¡ llena (o requerimos un poco mÃ¡s de anticipaciÃ³n). Â¿Te busco algo para maÃ±ana?",

    // Caso: No pidieron sede especÃ­fica y no hay nada hoy (pero no es domingo)
    GENERIC_NO_SLOTS: "No encontrÃ© espacios disponibles para hoy. Recuerda que Polanco atiende Lun/Mar/Vie y SatÃ©lite MiÃ©/Jue/SÃ¡b. Â¿Busco opciones para maÃ±ana?",

    INTRO: ["AquÃ­ tienes los horarios disponibles:", "EncontrÃ© estas opciones para ti:"],
    ASK: ["Â¿CuÃ¡l te acomoda mejor?", "Â¿Te gustarÃ­a reservar alguno?"]
  }
};

/* ===================== UTILIDADES ===================== */
function randomChoice(arr) { 
  return Array.isArray(arr) ? arr[Math.floor(Math.random() * arr.length)] : arr; 
}
function capitalize(str) { return str.charAt(0).toUpperCase() + str.slice(1); }

function formatSedeName(sede) { 
  return { 'POLANCO': 'Polanco', 'SATELITE': 'SatÃ©lite' }[sede] || capitalize(sede); 
}

function getWorkingDaysText(sede) {
  if (sede === 'POLANCO') return 'lunes, martes y viernes';
  if (sede === 'SATELITE') return 'miÃ©rcoles, jueves y sÃ¡bados';
  return 'dÃ­as hÃ¡biles';
}

function formatTime(iso, tz) {
  try {
    return new Intl.DateTimeFormat('es-MX', { 
      timeZone: tz, hour: 'numeric', minute: '2-digit', hour12: true 
    }).format(new Date(iso)).replace(' ', '').toLowerCase();
  } catch { return iso.substring(11, 16); }
}

function formatDate(iso, tz) {
  try {
    const d = new Date(iso);
    const day = new Intl.DateTimeFormat('es-MX', { timeZone: tz, weekday: 'long' }).format(d);
    const num = new Intl.DateTimeFormat('es-MX', { timeZone: tz, day: 'numeric', month: 'long' }).format(d);
    return `${capitalize(day)}, ${num}`;
  } catch { return iso; }
}

/* ===================== MAIN ===================== */
try {
  const items = $input.all();
  const inputData = items[0]?.json || {};
  let ctx = {};
  try { ctx = $('SET_DEFAULTS1').first()?.json || {}; } catch {}

  const TZ = ctx.tz || CONFIG.TZ_DEFAULT;
  // isToday viene de SET_DEFAULTS. Si no existe, calculamos.
  const isToday = ctx.isToday !== undefined ? ctx.isToday : false;

  if (inputData.error) throw new Error(inputData.errorMessage || "API Error");

  let slots = inputData.allowedSlots || inputData.scheduleGate?.allowedSlots || [];
  if (inputData.kind === 'avail' && inputData.allowedSlots) slots = inputData.allowedSlots;

  // â•â•â• LÃ“GICA DE RESPUESTA SI NO HAY HORARIOS â•â•â•
  if (!slots.length) {
    let msg = CONFIG.MESSAGES.GENERIC_NO_SLOTS;

    if (isToday) {
      // 1. QuÃ© dÃ­a es hoy (0-6)
      const now = new Date(new Date().toLocaleString("en-US", {timeZone: TZ}));
      const currentDOW = now.getDay();

      // 2. QuÃ© sede pidiÃ³ el usuario (si pidiÃ³ alguna)
      const sedeKey = (ctx.sedePreferida || '').toUpperCase();

      // --- ESCENARIO 1: ES DOMINGO ---
      if (currentDOW === 0) {
        msg = CONFIG.MESSAGES.SUNDAY_OFF;
      }
      // --- ESCENARIO 2: PIDIÃ“ UNA SEDE ESPECÃFICA ---
      else if (sedeKey && CONFIG.WORKING_DAYS[sedeKey]) {
        const opensToday = CONFIG.WORKING_DAYS[sedeKey].includes(currentDOW);

        if (!opensToday) {
          // La sede NO abre hoy (ej. SatÃ©lite en Lunes) -> ExplicaciÃ³n educativa
          msg = CONFIG.MESSAGES.WRONG_DAY_SEDE
            .replace('{sede}', formatSedeName(sedeKey))
            .replace('{dias}', getWorkingDaysText(sedeKey));
        } else {
          // La sede SÃ abre hoy, pero estÃ¡ llena o es tarde -> Regla de anticipaciÃ³n
          msg = CONFIG.MESSAGES.NO_SLOTS_TODAY
            .replace('{sede}', formatSedeName(sedeKey));
        }
      }
      // --- ESCENARIO 3: BÃšSQUEDA GENÃ‰RICA (Sin sede) ---
      else {
        // Si no pidiÃ³ sede y no encontrÃ³ nada, mantenemos el mensaje genÃ©rico 
        // o podrÃ­amos detectar si HOY no abre ninguna (raro si no es domingo)
        msg = CONFIG.MESSAGES.GENERIC_NO_SLOTS;
      }
    } else {
       // Si no es hoy (buscÃ³ fecha futura y no hubo), mensaje estÃ¡ndar
       msg = "No encontrÃ© horarios disponibles para esa fecha en particular. Â¿Te gustarÃ­a ver opciones cercanas?";
    }

    // Agregar nota tÃ©cnica si hubo error parcial (opcional)
    if (inputData.warnings?.sedesConError?.length > 0) {
      // Omitimos para el usuario final salvo debugging
      // msg += ` (Debug: Error en ${inputData.warnings.sedesConError.join(', ')})`;
    }

    return [{ json: { status: 'no_availability', suggestedResponse: msg, ctx } }];
  }

  // â•â•â• AGRUPACIÃ“N Y RESPUESTA CON HORARIOS â•â•â•
  const grouped = {};
  const order = []; 

  slots.forEach(slot => {
    const dateVisual = slot.dateFormatted || formatDate(slot.start, TZ);
    const key = `${dateVisual}|${slot.sede}`;

    if (!grouped[key]) {
      grouped[key] = { dateFmt: dateVisual, sede: slot.sede, slots: [] };
      order.push(key);
    }
    grouped[key].slots.push(slot);
  });

  let responseText = randomChoice(CONFIG.MESSAGES.INTRO) + '\\n';
  let slotsForBooking = [];
  let shownCount = 0;

  const keysToShow = order.slice(0, CONFIG.MAX_DAYS_TO_SHOW);

  keysToShow.forEach(key => {
    const group = grouped[key];
    responseText += `\\nğŸ“… *${group.dateFmt}* | ğŸ“ ${formatSedeName(group.sede)}\\n`;

    group.slots.forEach(slot => {
      if (shownCount >= CONFIG.MAX_SLOTS_TO_SHOW) return;
      const timeStr = formatTime(slot.start, TZ);
      responseText += `â€¢ ${timeStr}\\n`;
      slotsForBooking.push({
        id: slot.id,
        displayTime: timeStr,
        slotData: { start: slot.start, end: slot.end, sede: slot.sede }
      });
      shownCount++;
    });
  });

  if (slots.length > shownCount) responseText += '\\n(Hay mÃ¡s opciones disponibles...)';
  responseText += '\\n' + randomChoice(CONFIG.MESSAGES.ASK);

  return [{
    json: {
      status: 'success',
      suggestedResponse: responseText.trim(),
      allowedSlots: slots,
      slotsForBooking,
      ctx,
      version: VERSION
    }
  }];

} catch (err) {
  return [{ json: { status: 'error', suggestedResponse: "Tuve un problema tÃ©cnico. Â¿Intentamos de nuevo?", error: err.message } }];
}
