const messageAge = now - timestamp;

if (messageAge > 300) {
  return [{ json: { ...triggerData, _rate_limit_ok: false, _reason: 'mensaje_antiguo' } }];
}

return [{
  json: {
    ...triggerData,
    _rate_limit_ok: true,
    _telefono: message.from,
