// ğŸš« FILTRO DE STATUS UPDATES - ANTI-LOOP (UROBOT V2)
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const data = $input.first()?.json || {};

// Chat Trigger support
if (data.chatInput && !data.messages) {
  const mockPhone = '526674492872';
  return [{
    json: {
      messages: [{
        from: mockPhone,
        id: 'chat-trigger-' + Date.now(),
        timestamp: Math.floor(Date.now() / 1000).toString(),
        type: 'text',
        text: { body: data.chatInput }
      }],
      metadata: { phone_number_id: 'test-phone-id' },
      _fromChatTrigger: true,
      _sessionId: data.sessionId
    }
  }];
}

if (data.statuses && data.statuses.length > 0) return [];
if (!data.messages || data.messages.length === 0) return [];
const msg = data.messages[0];
if (!msg.from) return [];

const referral = msg.referral || {};
const esMetaAds = !!(referral.source_url || referral.headline || referral.body || referral.ctwa_clid);

return [{
  json: {
    ...data,
    es_meta_ads: esMetaAds,
    meta_source_url: referral.source_url || null,
    meta_headline: referral.headline || null,
    meta_body: referral.body || null,
    meta_ctwa_clid: referral.ctwa_clid || null,
    meta_source_id: referral.source_id || null
  }
}];

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// âš¡ RATE LIMIT CHECK (mensaje antiguo > 5 min)
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const triggerData = $input.first()?.json || {};
const messages = triggerData.messages || [];
const message = messages[0] || {};

if (!messages.length || !message.from) {
  return [{ json: { ...triggerData, _rate_limit_ok: false, _reason: 'no_message' } }];
}

const timestamp = parseInt(message.timestamp || '0');
const now = Math.floor(Date.now() / 1000);
const messageAge = now - timestamp;

if (messageAge > 300) {
  return [{ json: { ...triggerData, _rate_limit_ok: false, _reason: 'mensaje_antiguo' } }];
}

return [{
  json: {
    ...triggerData,
    _rate_limit_ok: true,
    _telefono: message.from,
    _mensaje_id: message.id
  }
}];

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// 1ï¸âƒ£ VALIDAR Y NORMALIZAR (UROBOT V2)
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const setTexto = $input.first()?.json || {};
const filterData = $('ğŸš« Filtrar Status').first()?.json || {};
const rateData = $('âš¡ Rate Limit Check').first()?.json || {};

const messages = filterData.messages || [];
const message = messages[0] || {};
const metadata = filterData.metadata || {};

const telefono = message.from || rateData._telefono || '';
const mensajeId = message.id || rateData._mensaje_id || 'msg-' + Date.now();
const msgType = message.type || 'text';
const fromChatTrigger = filterData._fromChatTrigger === true;
const sessionId = filterData._sessionId || mensajeId;

let mensajeUsuario = setTexto.mensajeUsuario || '';
if (!mensajeUsuario && msgType === 'text') {
  mensajeUsuario = message.text?.body || '';
}

const contacts = filterData.contacts || [];
let nombreUsuario = contacts[0]?.profile?.name || 'Usuario';

const esMetaAds = filterData.es_meta_ads === true;
const metaCtwaClid = filterData.meta_ctwa_clid || null;
const metaHeadline = filterData.meta_headline || null;
const metaSourceUrl = filterData.meta_source_url || null;

let fuente = 'organico';
if (esMetaAds) fuente = 'meta_ads';

let tipoMedia = 'texto';
switch (msgType) {
  case 'image': tipoMedia = 'imagen'; break;
  case 'audio': tipoMedia = 'audio'; break;
  case 'document': tipoMedia = 'documento'; break;
  default: tipoMedia = 'texto';
}

return [{
  json: {
    telefono,
    nombreUsuario,
    mensajeUsuario: mensajeUsuario.trim(),
    mensaje_id_externo: mensajeId,
    external_chat_id: telefono,
    tipoMedia,
    fuente,
    es_meta_ads: esMetaAds,
    meta_ctwa_clid: metaCtwaClid,
    meta_headline: metaHeadline,
    meta_source_url: metaSourceUrl,
    phone_number_id: metadata.phone_number_id || '',
    _fromChatTrigger: fromChatTrigger,
    _sessionId: sessionId,
    timestamp: message.timestamp || Math.floor(Date.now() / 1000).toString()
  }
}];

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// 5ï¸âƒ£ PROCESAR CONTEXTO (UROBOT V2)
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const normalizado = $('1ï¸âƒ£ Validar y Normalizar').first()?.json || {};
const convResult = $('2ï¸âƒ£ Upsert ConversaciÃ³n').first()?.json || {};
const ctxRawInput = $input.first()?.json?.contexto;

let ctx = {};
try {
  ctx = typeof ctxRawInput === 'string' ? JSON.parse(ctxRawInput) : (ctxRawInput || {});
} catch (e) {
  ctx = {};
}

const {
  historial_mensajes: historial = '',
  ultimo_mensaje_bot: ultimoBot = '',
  sintomas_acumulados: sintomasRaw = [],
  banderas_rojas: banderasPrev = [],
  lead_id: leadId = null,
  es_paciente: esPaciente = false,
  pregunto_precio: preguntoPrecio = false,
  funnel_stage: funnelPrevio = '01_contactado',
  total_mensajes: totalMensajes = 0,
  nombre: nombreContexto,
  temperatura = 'frio',
  score_total: scoreTotal = 0,
  es_urgencia: esUrgencia = false,
  tiene_cita_pendiente: tieneCita = false,
  cita_pendiente: citaPendiente = null,
  accion_recomendada: accionRecomendada = null,
  es_primera_interaccion: esPrimera = true
} = ctx;

const sintomasArray = Array.isArray(sintomasRaw) ? sintomasRaw : [];
const sintomasTexto = sintomasArray.join(', ');

const nombreFinal = nombreContexto && nombreContexto !== 'Usuario'
  ? nombreContexto
  : (normalizado.nombreUsuario || 'Usuario');

return [{
  json: {
    ...ctx,
    lead_id: leadId,
    telefono: normalizado.telefono,
    nombreUsuario: nombreFinal,
    nombre_confirmado: ctx.nombre_confirmado ? nombreContexto : null,
    mensajeUsuario: normalizado.mensajeUsuario,
    tipoMedia: normalizado.tipoMedia,
    phone_number_id: normalizado.phone_number_id,
    conversacion_id: convResult.conversacion_id,
    historial_mensajes: historial,
    ultimo_mensaje_bot: ultimoBot,
    sintomas_texto: sintomasTexto,
    sintomas_acumulados: sintomasArray,
    banderas_rojas: banderasPrev,
    es_paciente: esPaciente,
    pregunto_precio: preguntoPrecio,
    funnel_stage: funnelPrevio,
    total_mensajes: totalMensajes,
    temperatura,
    score_total: scoreTotal,
    es_urgencia: esUrgencia,
    tiene_cita_pendiente: tieneCita,
    cita_pendiente: citaPendiente,
    accion_recomendada: accionRecomendada,
    es_primera_interaccion: esPrimera,
    es_meta_ads: normalizado.es_meta_ads,
    meta_ctwa_clid: normalizado.meta_ctwa_clid,
    meta_headline: normalizado.meta_headline,
    fuente: normalizado.fuente,
    _fromChatTrigger: normalizado._fromChatTrigger
  }
}];

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// 9ï¸âƒ£ FORMATEAR CONTEXTO PARA AGENTE (UROBOT V2)
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const ctx = $input.first()?.json || {};

const historialBD = ctx.historial_mensajes || '';
const ultimoBot = ctx.ultimo_mensaje_bot || '';
const totalMensajes = ctx.total_mensajes || 0;
const esPrimera = ctx.es_primera_interaccion ?? (totalMensajes <= 1);

const nombreFinal = ctx.nombreUsuario || 'Usuario';
const tenemosNombre = nombreFinal !== 'Usuario';

let contextoContacto = '';

// Nombre
contextoContacto += `PACIENTE: ${nombreFinal}`;
if (!tenemosNombre) contextoContacto += ' (nombre desconocido â€” pregÃºntalo de forma natural)';
contextoContacto += '\n';

// Primera interacciÃ³n
if (esPrimera) {
  contextoContacto += 'âš¡ PRIMERA INTERACCIÃ“N â€” SÃ© especialmente cÃ¡lido y profesional\n';
}

// Scoring
contextoContacto += `ğŸ“Š Temp: ${ctx.temperatura || 'frio'} | Score: ${ctx.score_total || 0}/100 | Funnel: ${ctx.funnel_stage || '01_contactado'} | Msgs: ${totalMensajes}\n`;

// Cita pendiente
if (ctx.tiene_cita_pendiente && ctx.cita_pendiente) {
  const c = ctx.cita_pendiente;
  contextoContacto += `\nğŸ“… CITA PENDIENTE:\n- Fecha: ${c.fecha_hora}\n- Sede: ${c.sede || 'Por definir'}\n- Estado: ${c.estado} / ${c.confirmacion || 'sin confirmar'}\n- Tipo: ${c.tipo || 'consulta'}\n`;
}

// SÃ­ntomas
if (ctx.sintomas_acumulados?.length > 0) {
  contextoContacto += `\nğŸ©º SÃNTOMAS REPORTADOS: ${ctx.sintomas_texto || ctx.sintomas_acumulados.join(', ')}\n`;
}

// Banderas rojas
if (ctx.banderas_rojas?.length > 0) {
  contextoContacto += `\nğŸš¨ BANDERAS ROJAS: ${ctx.banderas_rojas.join(', ')}\n`;
}

if (ctx.es_urgencia) {
  contextoContacto += '\nâš ï¸ URGENCIA MÃ‰DICA â€” Priorizar atenciÃ³n inmediata\n';
}

if (ctx.pregunto_precio) {
  contextoContacto += '\nğŸ’° Ha preguntado por precios previamente\n';
}

if (ctx.accion_recomendada) {
  contextoContacto += `\nğŸ¯ AcciÃ³n recomendada: ${ctx.accion_recomendada}\n`;
}

// Meta Ads
if (ctx.es_meta_ads) {
  contextoContacto += '\nğŸ“± LlegÃ³ por anuncio de Facebook/Instagram';
  if (ctx.meta_headline) contextoContacto += ` â€” "${ctx.meta_headline}"`;
  contextoContacto += '\n';
}

// Historial BD
if (historialBD) {
  contextoContacto += `\nâ•â•â• HISTORIAL RECIENTE â•â•â•\n${historialBD}\nâ•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•\n`;
}

return [{
  json: {
    ...ctx,
    contextoContacto,
    origen: 'urobot'
  }
}];

